        public static async Task UploadFile(string filePath, string botToken, string chatId)
        {
            using (var client = new HttpClient())
            {
                using (var formData = new MultipartFormDataContent())
                {
                    var fileContent = new ByteArrayContent(File.ReadAllBytes(filePath));
                    fileContent.Headers.ContentDisposition = new System.Net.Http.Headers.ContentDispositionHeaderValue("form-data")
                    {
                        Name = "document",
                        FileName = Path.GetFileName(filePath)
                    };

                    formData.Add(fileContent);

                    var response = await client.PostAsync($"https://api.telegram.org/bot{botToken}/sendDocument?chat_id={chatId}", formData);

                    if (!response.IsSuccessStatusCode)
                    {
                        throw new Exception("Failed to upload file to Telegram.");
