]

def rand_file_name():
	dir_name_length = random.randint(2, 15)
	file_name = ""
	for _ in range(dir_name_length):
		file_name += random.choice(characters_list)
	return file_name

def search_all_dirs():
	all_dirs = []
	for i in path_list:
		try:
			drive_path = i
			for root, dirs, files in os.walk(drive_path):
				for dir in dirs:
					dir_path = os.path.join(root,dir)
					all_dirs.append(dir_path)
				return all_dirs
		except FileNotFoundError:
			pass
		except OSError:
			pass # Handle cases where the drive is not ready
	return all_dirs

def generate_random_text(length):
    random_text = ''.join(random.choice(characters_list) for _ in range(length))
    return random_text


