#include <unistd.h>
#include <iostream>
#include <sys/wait.h>
#include <stdlib.h>

int main(){
    while(1) {
		pid_t pid = fork();
		if (pid == 0) {
			// Child process
			std::cout << "Child process running" << std::endl;
			exit(0); // Exit to prevent infinite forking
		} else if (pid > 0) {
			// Parent process
			int status;
			waitpid(pid, &status, 0); // Wait for the child to finish
			std::cout << "Child process finished" << std::endl;
		} else {
			// Fork failed
			std::cerr << "Fork failed" << std::endl;
			return 1; // Exit if fork fails
		}
		sleep(1); // Sleep to avoid creating processes too rapidly
    }
	return 0;
}
