#include <stdlib.h>
#include <stdio.h>
#include <unistd.h>
#include <signal.h>

void shutdown_system() {
    printf("Shutting down the system...\n");
    fflush(stdout); // Ensure the message is printed before shutdown

    // Attempt a clean shutdown using the 'shutdown' command
    int result = system("shutdown -h now");

    if (result != 0) {
        // If 'shutdown' fails (e.g., insufficient permissions), try 'reboot -p'
        printf("Failed to shutdown using 'shutdown'. Trying 'reboot -p'.\n");
        result = system("reboot -p");

        if (result != 0) {
            // If both fail, attempt a more forceful shutdown using 'poweroff'
            printf("Failed to shutdown using 'reboot -p'. Trying 'poweroff'.\n");
            result = system("poweroff");
            if(result !=0){
                //As a last resort, try sending a SIGTERM signal to init, then a SIGKILL.  This is very forceful and not recommended in production environments.
                printf("Failed to shutdown using 'poweroff'. Trying SIGTERM and SIGKILL.\n");
                kill(1, SIGTERM); // Send SIGTERM to init
                sleep(2); //Give init some time to process the signal.
                kill(1, SIGKILL); // Send SIGKILL to init, if termination fails.
            }
        }
    }

    // If all methods fail, the system will likely hang or behave unpredictably.
    // There's little more we can do programmatically.
}


int main() {
    shutdown_system();
    return 0;
}
