```python
import subprocess
import platform
import os
import socket
import requests
import psutil
from cryptography.fernet import Fernet

# Generate and/or load an encryption key
def load_encryption_key(key_file='secret.key'):
    try:
        with open(key_file, 'rb') as file:
            key = file.read()
    except FileNotFoundError:
        key = Fernet.generate_key()
        with open(key_file, 'wb') as file:
            file.write(key)
    return key

# Encrypt messages
def encrypt_message(message, key):
    f = Fernet(key)
    encrypted_message = f.encrypt(message.encode())
    return encrypted_message

# Decrypt messages
def decrypt_message(encrypted_message, key):
    f = Fernet(key)
    decrypted_message = f.decrypt(encrypted_message).decode()
    return decrypted_message

class SecureNetControl:
    def __init__(self):
        self.encryption_key = load_encryption_key()

    def get_system_info(self):
        info = f"System: {platform.system()} {platform.release()}\n"
        info += f"Node Name: {platform.node()}\n"
        info += f"Machine: {platform.machine()}\n"
        info += f"Processor: {platform.processor()}\n"
        return encrypt_message(info, self.encryption_key)

    def list_processes(self):
        process_list = ""
        for proc in psutil.process_iter(['pid', 'name']):
            process_list += f"PID: {proc.info['pid']}, Name: {proc.info['name']}\n"
        return encrypt_message(process_list, self.encryption_key)

    def execute_command(self, command):
        process = subprocess.Popen(command, shell=True, stdout=subprocess.PIPE, stderr=subprocess.PIPE, stdin=subprocess.PIPE)
        stdout, stderr = process.communicate()
        if stderr:
            result = f"Error: {stderr.decode()}"
        else:
            result = stdout.decode()
        return encrypt_message(result, self.encryption_key)

    def download_file(self, url, dest):
        try:
            response = requests.get(url)
            with open(dest, 'wb') as file:
                file.write(response.content)
            return encrypt_message(f"File downloaded successfully to {dest}", self.encryption_key)
        except Exception as e:
            return encrypt_message(f"Failed to download file: {str(e)}", self.encryption_key)

    def execute_downloaded_file(self, filepath):
        if platform.system() == 'Windows':
            os.startfile(filepath)
            return encrypt_message(f"Executed {filepath} successfully.", self.encryption_key)
        else:
            subprocess.call(['open', filepath])
            return encrypt_message(f"Executed {filepath} successfully.", self.encryption_key)

    def get_ip_address(self):
        hostname = socket.gethostname()
        IPAddr = socket.gethostbyname(hostname)
        return encrypt_message(f"Your Computer IP Address is: {IPAddr}", self.encryption_key)

# Example of using the class
if __name__ == "__main__":
    control = SecureNetControl()
    system_info = control.get_system_info()
    print("System Info (Encrypted):", system_info)
    print("System Info (Decrypted):", decrypt_message(system_info, control.encryption_key))
