import time
import requests

def continuous_http_connection_attempt(target_ip, sleep_time): 
    while True:
        try:
            response = requests.get(target_ip)
            if response.status_code == 200:
                print("Connection established to the target IP '{}' and the request was successful.".format(target_ip))
            else:
                print("Connection established to the target IP '{}' but the request was not successful. Status code: {}".format(target_ip, response.status_code))
        except requests.ConnectionError as e:
            print("Failed to establish connection to the target IP '{}'. Reason: {}. Retry after {} seconds.".format(target_ip, str(e), sleep_time))
            time.sleep(sleep_time)
