            frame = camera.get_frame()
            if frame is not None:
                cv2.imshow('Background Camera Feed', frame)

            if cv2.waitKey(1) & 0xFF == ord('q'):
                break

    finally:
        camera.stop()
        cv2.destroyAllWindows()
