def dms_to_dd(degrees, minutes, seconds, direction):
    """
    Converts degrees, minutes, seconds (DMS) to decimal degrees (DD).
    """
    dd = degrees + minutes/60 + seconds/(60*60)
    if direction in ('S', 'W'):
        dd *= -1
    return dd

