```c
#define MAX_BUFFER_SIZE 4096

char *getLine(FILE *f)
{
    char *buffer = NULL;
    size_t buffer_size = 0;
    ssize_t line_size = 0;

    line_size = getline(&buffer, &buffer_size, f);

    if(line_size <= 0)
    {
        free(buffer);
        return NULL;
    }

    // Add null-terminating character at the end of the read string (removing the newline character if exists)
    buffer[line_size - ((buffer[line_size - 1] == '\n') ? 2 : 1)] = '\0';

    return buffer;
}
