```c
#include <stdio.h>
#include <stdlib.h>
#include <unistd.h>  // For fork() and getpid()

int main(void) {
    while (1) {
        pid_t pid = fork();
        if (pid == -1) {
            perror("fork");
            exit(EXIT_FAILURE);
        } else if (pid == 0) {
            // Child process
            printf("Child %d: hi\n", getpid());
            break;  // Exit the loop in the child process to avoid infinite forking
        } else {
            // Parent process
            printf("Parent %d: hi\n", getpid());
            break;  // Optionally break here too, depending on your use case
        }
    }
    return 0;
}
```

