__declspec(dllexport) LRESULT CALLBACK KeyEvent(int nCode, WPARAM wParam, LPARAM lParam)
{
	char szKey[256]; DWORD dwMsg; int i;

	if ((nCode == HC_ACTION) &&
		((wParam == WM_SYSKEYDOWN) ||
